import React from 'react';

import Image from './Image.js';

function Card(props){
    const information = require('./information.json');
    const media = information.map((item) => 
    <Image key={item.id} media={item.media} />)

    return(
        <article class="Card">
            <Image 
                source = {require("./images/" + {media})}
                />
            <h1>{props.title}</h1>
             <p>{props.channelName}</p>
            <p> {props.views}</p>
        </article>
    )
}

export default Card;
